            method: 'PUT',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ id , Name  , Price  })
        })

        console.log("okkkkkkk",response);

};

    useEffect(()=>{
        Save();
    },[]);

// console.log(SignusetData);
