import Header from "../header/formheader";
import { Slide } from 'react-slideshow-image';
export default function Addevent(){
        const images = [
                "https://images.pexels.com/photos/952478/pexels-photo-952478.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=2",
                "https://images.pexels.com/photos/952478/pexels-photo-952478.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=2",
                "https://images.pexels.com/photos/952478/pexels-photo-952478.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=2"
            ];

const spanStyle = {
        padding: '20px',
        background: '#efefef',
        color: '#000000'
      }

      const divStyle = {
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        backgroundSize: 'cover',
        height: '400px'
      }
      const slideImages = [
        {
          url: 'https://images.unsplash.com/photo-1509721434272-b79147e0e708?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1500&q=80',
          caption: 'Slide 1'
        },
        {
          url: 'https://images.unsplash.com/photo-1506710507565-203b9f24669b?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1536&q=80',
          caption: 'Slide 2'
        },
        {
          url: 'https://images.unsplash.com/photo-1536987333706-fc9adfb10d91?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1500&q=80',
          caption: 'Slide 3'
        },
      ];

      const Slideshow = () => {
          return (
            <div className="slide-container">
              <Slide>
               {slideImages.map((slideImage, index)=> (
                  <div key={index}>
                    <div style={{ ...divStyle, 'backgroundImage': `url(${slideImage.url})` }}>
                      <span style={spanStyle}>{slideImage.caption}</span>
                    </div>
                  </div>
                ))} 
              </Slide>
            </div>
          )
      }
    return(
        <>
        <Header/>

        <div className="border_catagory">


<p>
        <p className="heading_event">Event Name </p>
        <input className="add" type="text" placeholder="Brand Name"/>
</p>
<p className="adc2">
        <p className="heading_event">Event Image </p>
        <input className="file" type="file" placeholder="Brand Name"/>
</p>
<p>
        <p className="heading_event">Discounts  </p>
        <input className="add" type="text" placeholder="Discounts"/>
</p>

<button>Upload</button>


         </div>


<div>
       <h1>example</h1> 


       <Slide>
            <div className="each-slide-effect">
                <div  style={{ 'backgroundImage': `url(${images[0]})` , width:"100%" }}>
                    <span>Slide 1</span>
                </div>
            </div>
            <div className="each-slide-effect">
                <div  style={{ 'backgroundImage': `url(${images[0]})` , width:"100%" }}>
                    <span>Slide 2</span>
                </div>
            </div>
        </Slide>

</div>

        </>

    )
}
