import Header from "../header/formheader";
import {Baseurl} from "../url/baseurl"
import { useState , useEffect } from "react";
export default function Addfooter(){

        const [ ViewHeader , setViewproduct ] = useState([])
        const Show = () =>{

            fetch('http://localhost:8082/get-data-footer')
          .then(function(response){
            return response.json(); })
          .then(function(data){
            setViewproduct(data); 
        })
        }


        const [ Addheader , setAddheader ] = useState({})
        const [ imgg , setimg ] = useState({})

const Addh = async (e)=>{

        const id= Addheader.id;
        const name= Addheader.name;
        const link= Addheader.link;
        const photo = imgg;
        console.log('ffffff',photo); 

        const response = await fetch(`${Baseurl}/add`, {
            method: 'POST',
        headers: { 'Content-Type': 'application/json'},
        body: JSON.stringify({id , name , link , photo})
        });
        console.log("okkkk",response);

};


    return(
        <>
         <Header/>
         <div className="flex">


         <div className="border_footer">

            <h3>Footer links and texts</h3>
<p>
        <p className="heading_footer">Footer Heading</p>
        <input className="add" type="text" placeholder="Footer Heading"/>
</p>
<p>
        <p className="heading_footer">Footer name</p>
        <input className="add" type="text" placeholder="Footer name"/>
</p>
<p>
        <p className="heading_footer">Footer link</p>
        <input className="add" type="text" placeholder="Footer link"/>
</p>

<button>Upload</button>

         </div>

         <div className="border_footer">

            <h3>logo and icons</h3>
<p>
        <p className="heading_footer">Position </p>
        <input className="add" type="text" placeholder="Position "/>
</p>
<p>
        <p className="heading_footer">Footer Logo</p>
        <input className="add" type="file" placeholder="Footer Logo"/>
</p>
<p>
        <p className="heading_footer">Logo link</p>
        <input className="add" type="text" placeholder="Logo link"/>
</p>

<button>Upload</button>

         </div>

         </div>
        </>
    )
}
