import Header from "../addition page /header";
import React, { useRef } from 'react';
import emailjs from '@emailjs/browser';

export default function Contactme(){
    const form = useRef();

    const sendEmail = (e) => {
      e.preventDefault();

      emailjs
        .sendForm('YOUR_SERVICE_ID', 'YOUR_TEMPLATE_ID', form.current, {
          publicKey: 'YOUR_PUBLIC_KEY',
        })
        .then(
          () => {
            console.log('SUCCESS!');
          },
          (error) => {
            console.log('FAILED...', error.text);
          },
        );
    };
    return(
        <>
        <Header/>
        <form className="flex" ref={form} onSubmit={sendEmail}>
            <h1 className="message">Message Me</h1>
      <label>Name</label>
            <input type="text" placeholder="Full Name"></input>
      <label>Email</label>
            <input type="text" placeholder="Email"></input>
      <label>Number</label>
            <input type="text" placeholder="Contact number"></input>

      <label>Message</label>
            <textarea className="enquery" placeholder="Any enquery"></textarea>
        </form>
        </>
    )
}
